use sistema_biblioteca;

--1. Procedure para retornar todos os livros de sociologia.-- views-- 1. View para listar todos os livros com seus respectivos autores e ano de publicação.create view vw_livros_autores_e_anoasselect titulo, autor, ano_publicacao from LIVROselect * from vw_livros_autores_e_ano;-- 2 View para exibir os detalhes dos funcionários que são gerentes.create view vw_funcionarios_gerentesasselect * from FUNCIONARIO where matricula like '%G%';select * from vw_funcionarios_gerentes;-- 3 . View para mostrar todos os eventos organizados por data.create view vw_eventos_por_dataasselect * from EVENTO order by data select * from vw_eventos_por_data;-- 4. View para listar os livros de cada categoria (Tecnologia, Sociologia, Ciência).create view vw_livros_por_categoriaas select * from LIVRO  join TECNOLOGIA on (LIVRO.numero_registro = TECNOLOGIA.numero_registro);  select * from LIVRO join SOCIOLOGIA on (LIVRO.numero_registro = SOCIOLOGIA.numero_registro); select * from LIVRO  join CIENCIA on (LIVRO.numero_registro = CIENCIA.numero_registro);select * from vw_livros_por_categoriaselect * from LIVROselect * from TECNOLOGIAselect * from CIENCIA